    private void crEuroKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_crEuroKeyTyped
        try {
            double rate = Double.parseDouble(crEuro.getText());
            double dollars = Double.parseDouble(tfDollar.getText());
            double nwEuro = rate * dollars;
            tfEuro.setText(String.format("%.2f", nwEuro));
        } catch (Exception e) {
            tfEuro.setText(" ");
        }

    }//GEN-LAST:event_crEuroKeyTyped

