    private void btnDollarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnDollarMouseClicked

        try {
            double dollar = Double.parseDouble(tfDollar.getText());

            double euro = dollar * Double.parseDouble(crEuro.getText());
            tfEuro.setText(String.format("%.2f", euro));

            double gbp = dollar * Double.parseDouble(crGBP.getText());
            tfGBP.setText(String.format("%.2f", gbp));

            double yen = dollar * Double.parseDouble(crYen.getText());
            tfYen.setText(String.format("%.2f", yen));

            double nis = dollar * Double.parseDouble(crNIS.getText());
            tfNIS.setText(String.format("%.2f", nis));

        } catch (Exception e) {
            tfDollar.setText("");
        }

    }//GEN-LAST:event_btnDollarMouseClicked

    private void btnEuroMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnEuroMouseClicked
        try {
            double euro = Double.parseDouble(tfEuro.getText());

            double dollar = euro / Double.parseDouble(crEuro.getText());
            tfDollar.setText(String.format("%.2f", dollar));

            double gbp = dollar * Double.parseDouble(crGBP.getText());
            tfGBP.setText(String.format("%.2f", gbp));

            double yen = dollar * Double.parseDouble(crYen.getText());
            tfYen.setText(String.format("%.2f", yen));

            double nis = dollar * Double.parseDouble(crNIS.getText());
            tfNIS.setText(String.format("%.2f", nis));

        } catch (Exception e) {
            tfEuro.setText("");
        }
    }//GEN-LAST:event_btnEuroMouseClicked

